import { StrictMode, useContext, Suspense, lazy, useEffect, useState } from 'react';

import { AuthContext } from './AuthContext';

import { Spinner } from './components/Spinner';
import { useQuery, gql } from '@apollo/client';
import { map } from 'lodash'
import { List, ListItem, Checkbox, Avatar, ListItemButton, ListItemAvatar, ListItemText } from '@mui/material';
import { Box } from '@mui/system';


export const App = () => {
	const { isAuth } = useContext(AuthContext);
	const { userData } = useContext(AuthContext);
	const [tokens, setTokens] = useState([])

	const GET_DOGS = gql`
	{ 
		tokens(orderBy: tradeVolume orderDirection:desc, first: 5){
			id
			name
			symbol
			tradeVolume
		}
	}	
`;
	const {data, loading} = useQuery(GET_DOGS)

	useEffect(() => {
		if(!loading){
			setTokens(data.tokens)
		}
	},[loading])

	useEffect(() => {
		console.log(tokens)
	},[tokens])

	const renderList = () => <List>
	{map(tokens, (token) => {
		return(
			<ListItem
				secondaryAction={
					<Checkbox
						edge="end"
					/>
				}
				disablePadding
			>
				<ListItemButton>
					<ListItemAvatar>
						<Box style={{backgroundColor: 'gray', display: 'inline-block'}}>
							{token.symbol}
						</Box>
					</ListItemAvatar>
					<ListItemText primary={token.name} />
				</ListItemButton>
			</ListItem>
			)
		})}
		</List>
	return (
	<div>
		{
			!loading 
				? <div>
					<h1>Top 5 Tokens by Trade Volume in USD</h1>
					{renderList()}
				</div>
				: <Spinner/>
		}
	</div>
	);
};
