import { useEffect, useState } from 'react';
import { Spinner } from './components/Spinner';
import { useQuery, gql } from '@apollo/client';
import { map } from 'lodash'
import { List, Tabs, Tab } from '@mui/material';
import { Box } from '@mui/system';
import { TokenListItem } from './components/TokenListItem/TokenListItem'
export const App = () => {
	const [tokens, setTokens] = useState([])

	const GET_DOGS = gql`
	{ 
		tokens(orderBy: tradeVolume orderDirection:desc, first: 5){
			id
			name
			symbol
			tradeVolume
		}
	}	
`;
	const {data, loading} = useQuery(GET_DOGS)

	useEffect(() => {
		if(!loading){
			setTokens(data.tokens)
		}
	},[loading])

	useEffect(() => {
		console.log(tokens)
	},[tokens])

	const renderList = () => (<List>
		{map(tokens, (token) => <TokenListItem token={token}/>)}
	</List>)
return (
	<div>
		{
			!loading 
				? <Box sx={{display: 'flex', flexDirection: 'column'}}>
						<h1>Top 5 Tokens by Trade Volume in USD</h1>
						{renderList()}
						<Tabs value={value} onChange={handleChange} aria-label="basic tabs example">
							<Tab label="Item One" />
							<Tab label="Item Two" />
							<Tab label="Item Three"/>
						</Tabs>
				</Box>
				: <Spinner/>
		}
	</div>
	);
};
