import { useEffect, useState } from 'react';
import { Spinner } from './components/Spinner';
import { useQuery, gql } from '@apollo/client';
import { map } from 'lodash'
import { List, Tabs, Tab } from '@mui/material';
import { Box } from '@mui/system';
import { TokenListItem } from './components/TokenListItem/TokenListItem'
export const App = () => {
	const [tokens, setTokens] = useState([])

	const GET_DOGS = gql`
	{ 
		tokens(orderBy: tradeVolume orderDirection:desc, first: 5){
			id
			name
			symbol
			tradeVolume
		}
	}	
`;
	const {data, loading} = useQuery(GET_DOGS)
	const [tabValue, setTabValue] = useState(0)
	const [selectedToken, setSelectedToken] = useState()

	useEffect(() => {
		if(!loading){
			setTokens(data.tokens)
		}
	},[loading])

	useEffect(() => {
		console.log(selectedToken)
	},[selectedToken])

	const renderList = () => (<List>
		{map(tokens, (token) => <TokenListItem token={token} handleClick={setSelectedToken}/>)}
	</List>)

	const a11yProps = (index) => {
		return {
			id: `simple-tab-${index}`,
			'aria-controls': `simple-tabpanel-${index}`,
		}
	}

	const handleChange = (event, newValue) => {
		setTabValue(newValue);
	};
	return (
		<div>
			{
				!loading 
					? <Box sx={{display: 'flex', flexDirection: 'column'}}>
							<h1>Top 5 Tokens by Trade Volume in USD</h1>
							{renderList()}
						{	
							selectedToken
							? <>
								<h2>Show Chart For</h2>
								<Tabs value={tabValue} onChange={handleChange} aria-label="basic tabs example">
									<Tab label="Hourly" {...a11yProps(0)} />
									<Tab label="Daily" {...a11yProps(1)} />
									<Tab label="Monthly" {...a11yProps(2)} />
								</Tabs>
							</>
							: <h2>Select a Token to see the Total Liquidity in USD</h2>
						}
					</Box>
					: <Spinner/>
			}
		</div>
	);
};
