import { StrictMode, useContext, Suspense, lazy, useEffect, useState } from 'react';

import { Router, Redirect } from '@reach/router';

import { AuthContext } from './AuthContext';

import { Login } from './pages/Login';
import { Registration } from './pages/Registration';
import { Logout } from './pages/Logout';
import { Page404 } from './pages/Page404';

import { NavBar } from './components/NavBar';
import { Footer } from './components/Footer';
import { Spinner } from './components/Spinner';
import { useQuery, gql } from '@apollo/client';

const Home = lazy(() => import('./pages/Home'));
const UserAdministration = lazy(() => import('./pages/UserAdministration'));


export const App = () => {
	const { isAuth } = useContext(AuthContext);
	const { userData } = useContext(AuthContext);
	const [tokens, setTokens] = useState([])

	const GET_DOGS = gql`
	{ 
		tokens(orderBy: tradeVolume orderDirection:desc, first: 5){
			id
			name
			symbol
			tradeVolume
		}
	}	
`;
	const {data, loading} = useQuery(GET_DOGS)

	useEffect(() => {
		setTokens(data.tokens)
	},[loading])

	useEffect(() => {
		loading !== false && console.log(tokens)
	},[tokens])

	return (
	<div>

	</div>
	);
};
